 <input type="radio" name="shipping_method" value="{{ quote.code }}" {% if quote.lockers %} id="select_locker" {% endif %} />

{% if quote.lockers %}
    <script>
    (function() {
        let nextDayVal = document.getElementById("select_locker").value;
        document.querySelector('#select_locker').addEventListener('click',function () {
            window.LockerPlugin.init();
            let plugin = window.LockerPlugin.getInstance();
            plugin.open();
            plugin.subscribe((locker) => {
                let lockerValue = null;
                if (typeof locker.lockerId !== 'undefined') {
                    lockerValue = locker.lockerId;
                }
                document.getElementById("select_locker").value = nextDayVal + "." + lockerValue;
                plugin.close();
            });
        });
    })();
    </script>
 {% endif %}
